function TechToolContainer({ name, svg }) {
  return (
    <div className="flex flex-col items-center justify-center ">
      <div className="h-16 flex items-center justify-center pb-4">{svg}</div>
      <p className="custom-p">{name}</p>
    </div>
  );
}

function TechStack() {
  const techTools = [
    {
      name: "Javascript",
      image: js,
      svg: <JSsvg />,
    },
    {
      name: "React",
      image: r,
      svg: <Reactsvg />,
    },
    {
      name: "Tailwind",
      image: tw,
      svg: <Tailwindsvg />,
    },
    {
      name: "Figma",
      image: f,
      svg: <Figmasvg />,
    },
    {
      name: "Adobe Illustrator",
      image: ai,
      svg: <AIsvg />,
    },
  ];

  return (
    <>
      <div id="tools" className="container w-screen max-w-full flex items-center justify-center flex-col px-4 py-6 md:py-10 bg-[#EDEDF3]">
        <h2 className="custom-h2">Tech Stack</h2>
        <p className="custom-p py-4">
          This are the tools I use to design and develop websites and apps.
        </p>
        <div className="py-4 flex flex-wrap md:flex-nowrap items-center justify-center gap-8">
          {techTools.map((tool, index) => {
            return <TechToolContainer key={index} name={tool.name} svg={tool.svg} />;
          })}
        </div>
      </div>
    </>
  );
}

export default TechStack;
